# cyber_security_tool/broken_link_checker.py
import requests

class BrokenLinkChecker:
    def __init__(self, url):
        self.url = url

    def check(self):
        broken_links = []
        print(f"Checking for broken links on {self.url}")
        try:
            response = requests.get(self.url)
            if response.status_code == 404:
                broken_links.append(self.url)
        except requests.RequestException as e:
            print(f"Error checking link: {e}")
        return broken_links
