# cyber_security_tool/subdomain_enumeration.py

class SubdomainEnumerator:
    def __init__(self, domain, wordlist_path='small_wordlist.txt'):
        self.domain = domain
        self.wordlist_path = wordlist_path

    def enumerate(self):
        subdomains = []
        print(f"Enumerating subdomains for {self.domain} using {self.wordlist_path}")
        with open(self.wordlist_path, 'r') as wordlist:
            for word in wordlist:
                subdomain = f"{word.strip()}.{self.domain}"
                # You can add DNS resolution logic here
                subdomains.append(subdomain)
        return subdomains
